<script type="text/javascript">
$(document).ready(function (e) {
    $('.service_check').on('ifChecked', function(event){
        i_ser = $(this).val();
        $("#service_price_"+i_ser).removeAttr("disabled");
    });
    $('.service_check').on('ifUnchecked', function(event){
        i_ser = $(this).val();
        $("#service_price_"+i_ser).attr("disabled","disabled");
    });
});
function change_tour_price(i_list,night){
    $("#min_night_"+i_list).val(night).change();
    $("#max_night_"+i_list).val(night).change();
}
function add_price_room(room_id,i_list,i_range,i_price){
    var i_room = $('#total_price_room_'+i_list+'_'+i_range).val();
    i_room = parseInt(i_room) + 1;
    $('#total_price_room_'+i_list+'_'+i_range).val(i_room);
    $('#t_price_room_'+i_list+'_'+i_range).val(i_room);
    var str_room = i_list+'_'+i_range+'_'+i_room;
    $.ajax({
       url:SITE_URL+'/ajax-price-tour.php?mod=add_price_room&room_id='+room_id+'&str_room='+str_room,
       type: 'GET',
       data: "",
       error: function(){
         alert('error!');
       },
       success: function(data){
       $(".no_price_room_"+i_list+'_'+i_range+'_'+room_id+':last').after(data);
        toastr.success("Add Promotion","Successfully!");
       }
    });
}
function del_price_room(price_id,i_list,i_range,i_price){
    var check_confirm = confirm("Are you sure to delete?");
    if(check_confirm == true){
        var i_room = $('#t_price_room_'+i_list+'_'+i_range).val();
        i_room = parseInt(i_room) - 1;
        $('#t_price_room_'+i_list+'_'+i_range).val(i_room);
        $("#box_price_room_"+i_list+'_'+i_range+'_'+i_price).remove();
        del_id = $('#del_price_id').val();
        $('#del_price_id').val(del_id+","+price_id);
        if(i_room==0){
            $("#box_date_range_"+i_list+'_'+i_range).remove();
            $("#total_price_room_"+i_list+'_'+i_range).remove();
            $("#t_price_room_"+i_list+'_'+i_range).remove();
            //tru date range
            var i_range = $('#total_date_rage_'+i_list).val();
            i_range = parseInt(i_range) - 1;
            $('#total_date_rage_'+i_list).val(i_range);
        }
        toastr.success("Deleted Room","Successfully!");
    }
}
function del_price_room_new(i_list,i_range,i_price){
    var check_confirm = confirm("Are you sure to delete?");
    if(check_confirm == true){
        var i_room = $('#t_price_room_'+i_list+'_'+i_range).val();
        i_room = parseInt(i_room) - 1;
        $('#t_price_room_'+i_list+'_'+i_range).val(i_room);
        $("#box_price_room_"+i_list+'_'+i_range+'_'+i_price).remove();
        if(i_room==0){
            $("#box_date_range_"+i_list+'_'+i_range).remove();
            $("#total_price_room_"+i_list+'_'+i_range).remove();
            $("#t_price_room_"+i_list+'_'+i_range).remove();
            //tru date range
            var i_range = $('#total_date_rage_'+i_list).val();
            i_range = parseInt(i_range) - 1;
            $('#total_date_rage_'+i_list).val(i_range);
        }
        toastr.success("Deleted Room","Successfully!");
    }
}
//ADD NEW DATE RANGE
function add_date_range(t_id,i_list,date_range,min_night,max_night){
    if(date_range==undefined){
        date_range = "";
        txt_noti = "Add Date Range";
    }else{
        txt_noti = "Copy Date Range";    
    }
    if(min_night==undefined){
        min_night = "";
    }
    if(max_night==undefined){
        max_night = "";
    }
    if(t_id==undefined){
        t_id = "";
    }
    var i_range = $('#total_date_rage_'+i_list).val();
    i_range = parseInt(i_range) + 1;
    $('#total_date_rage_'+i_list).val(i_range);
    $('#t_date_rage_'+i_list).val(i_range);
    $.ajax({
       url:SITE_URL+'/ajax-price-tour.php?mod=add_date_range&t_id='+t_id+'&i_list='+i_list+'&i_range='+i_range+'&date_range='+date_range+'&min_night='+min_night+'&max_night='+max_night,
       type: 'GET',
       data: "",
       error: function(){
         alert('error!');
       },
       success: function(data){
        $("#list_date_rage_"+i_list).append(data);
        toastr.success(txt_noti,"Successfully!");
       }
    });
}
function del_date_range(date_range,i_list,i_range){
    var check_confirm = confirm("Are you sure to delete?");
    if(check_confirm == true){
        var i_price = $('#t_price_room_'+i_list+'_'+i_range).val();
        i_price = parseInt(i_price);
        for(i=1;i<=i_price;i++){
            $("#box_price_room_"+i_list+'_'+i_range+'_'+i).remove();
        }
        $("#box_date_range_"+i_list+'_'+i_range).remove();
        $("#box_price_service_"+i_list+'_'+i_range).remove();
        $("#total_price_room_"+i_list+'_'+i_range).remove();
        $("#t_price_room_"+i_list+'_'+i_range).remove();
        del_id = $('#del_date_range_'+i_list).val();
        $('#del_date_range_'+i_list).val(del_id+"|"+date_range);
        var i_range = $('#t_date_rage_'+i_list).val();
        i_range = parseInt(i_range) - 1;
        $('#t_date_rage_'+i_list).val(i_range);
        if(i_range==0){
            $("#list_price_"+i_list).hide();
        }
        toastr.success("Delete Date Range","Successfully!");
    }
}
function del_date_range_new(i_list,i_range){
     var check_confirm = confirm("Are you sure to delete?");
    if(check_confirm == true){
        var i_price = $('#t_price_room_'+i_list+'_'+i_range).val();
        i_price = parseInt(i_price);
        for(i=1;i<=i_price;i++){
            $("#box_price_room_"+i_list+'_'+i_range+'_'+i).remove();
        }
        $("#box_date_range_"+i_list+'_'+i_range).remove();
        $("#box_price_service_"+i_list+'_'+i_range).remove();
        $("#total_price_room_"+i_list+'_'+i_range).remove();
        $("#t_price_room_"+i_list+'_'+i_range).remove();
        var i_range = $('#t_date_rage_'+i_list).val();
        i_range = parseInt(i_range) - 1;
        $('#t_date_rage_'+i_list).val(i_range);
        if(i_range==0){
            $("#list_price_"+i_list).remove();
            var i_list_price = $('#total_list_price').val();
            i_list_price = parseInt(i_list_price) - 1;
            $('#total_list_price').val(i_list_price);
        }
        toastr.success("Deleted Date Range","Successfully!");
    }
}
//ADD NEW LIST PRICE
function add_new_price(t_id){
    var i_list = $('#total_list_price').val();
    i_list = parseInt(i_list) + 1;
    $('#total_list_price').val(i_list);
    $.ajax({
       url:SITE_URL+'/ajax-price-tour.php?mod=add_new_price&t_id='+t_id+'&i_list='+i_list,
       type: 'GET',
       data: "",
       error: function(){
         alert('error!');
       },
       success: function(data){
        $("#price_room #accordion").append(data);
        toastr.success("Add New Price","Successfully!");
       }
    });
}
